import React, { useEffect, useLayoutEffect, useRef, useState } from 'react';
import { OrgChart } from 'd3-org-chart';
import * as d3 from 'd3';

export const OrgChartComponent = (props, ref) => {
  const d3Container = useRef(null);
  const [chart, setChart] = useState(new OrgChart());
  // const chart = new OrgChart();
  const [url, setUrl] = useState(require('./data/org.csv'));
  const [data, setData] = useState(null);

  function addNode(node) {
    chart.addNode(node);
  }

  props.setClick(addNode);
  const borderColor = {
    "IT staff": "#EB2544",
    "Contract Vendors": "blue",
    "Wipro": "green"
  }

  useEffect(() => {

  }, [url]);

  // We need to manipulate DOM
  useLayoutEffect(() => {
    d3.csv(url
    ).then((data) => {
      setData(data);
      if (data && d3Container.current) {
        // if (!chart) {
        //   chart = new OrgChart();
        // }
        chart
          .container(d3Container.current)
          .data(data)
          .nodeWidth((d) => 280)
          .nodeHeight((d) => 60)
          .childrenMargin((d) => 20)
          .compactMarginBetween((d) => 15)
          .compactMarginPair((d) => 80)
          .onNodeClick((d, i, arr) => {
            console.log(d, 'Id of clicked node ');
            props.onNodeClick(d);
          })
          .nodeContent((d, i, arr, state) => {
            return `
                <div style="background-color:none;margin-left:1px;height:${d.height
              }px;border-radius:2px;overflow:visible">
                  <div style="height:${d.height
              }px;padding-top:0px;background-color:white;border:3px solid ${borderColor[d.data.company]};">

                   <div style="padding:10px; text-align:center">
                       <div style="color:#EB2544;font-size:16px;font-weight:bold"> ${d.data.name
              } </div>
                       <div style="color:#404040;font-size:12px;margin-top:4px"> ${d.data.positionName
              } </div>`
          //      +
          //     //<div style="color:#404040;font-size:12px;margin-top:4px"><b>Specialization:</b> ${d.data.skill
          //     // } </div>  
          //     // <div style="color:#404040;font-size:12px;margin-top:4px"><b>Project:</b> ${
          //     //   d.data.currentProject
          //     // } </div>
          //     // <div style="color:#404040;font-size:12px;margin-top:4px"><b>Project End date:</b> ${
          //     //   d.data.projectEndDate
          //     // } </div>
          //     // ${d.data.company !== 'rakbank'?'<div style="color:#404040;font-size:12px;margin-top:4px"><b>Contract End date:</b>'+d.data.contractEndDate+'</div>':''}
          //     `</div> 
          //          <div style="display:flex;justify-content:space-between;padding-left:15px;padding-right:15px;">
          //            <div> Manages:  ${d.data._directSubordinates} ðŸ‘¤</div>  
          //            <div> Oversees: ${d.data._totalSubordinates} ðŸ‘¤</div>    
          //          </div>
          //         </div>     
          // </div>`
          ;
          })
          .compact(true)
          .render()
          .expandAll()
          .fit();
      }
    });

  }, [url, d3Container.current]);

  return (
    <div>
      <div>
        <span>Data File:</span>
        <input type='url' placeholder='Enter CSV file URL' onChange={(e) => setUrl(e.target.value === "" ? require('./data/org.csv') : e.target.value)}></input>
        <button
          onClick={() => chart.exportImg({ full: true })}
          className="btn btn-action-button waves-effect waves-light"
        >
          export full img
        </button>
      </div>
      <div ref={d3Container} style={{ height: '100%', width: '100%', backgroundColor: '#f6f6f6' }} />
    </div>
  );
};
